<?php
namespace MBCPT;

class Menu {
	public function __construct() {
		add_action( 'admin_menu', [ $this, 'add_menu_page' ] );
	}

	public function add_menu_page(): void {
		add_menu_page(
			__( 'Meta Box', 'mb-custom-post-type' ),
			__( 'Meta Box', 'mb-custom-post-type' ),
			'manage_options',
			'meta-box',
			'__return_null',
			'data:image/svg+xml;base64,'
		);
	}
}